package com.company;

public class FindGene {

    // DNA strings
    String brca1line = "";
    String testString = "12312atg1231atg2312taa312312312tga3123tag123taa12atg3123123123taa12taa312tga3123tga12tga312tag3123123atg12312312taa31231taa23123tag123tag123taa12tga3tga12tga3123taa1231atg23";
    String GRch38dnapart = "";

    // This function will return the next gene from a string after "index"
    public String searchString(String string, int index) {

        // ATG is the start codon
        int startIndex = string.indexOf("atg",index);

        // If there are no more ATG's, return an empty string
        if(startIndex == -1){
            return "";
        }

        // Find the different stop codons -> TAA, TAG and TGA
        int taaIndex = findStopCodon(string, startIndex,"taa");
        int tagIndex = findStopCodon(string, startIndex,"tag");
        int tgaIndex = findStopCodon(string, startIndex,"tga");

        // Find the condon with the shortest distance from the start codon
        // Important to remove the codons that is not available (-1)
        int minIndex = 0;

        if(taaIndex == -1 || (tgaIndex != -1 && tgaIndex < taaIndex)){
            minIndex = tgaIndex;
        } else {
            minIndex = taaIndex;
        }

        if(minIndex == -1 || (tagIndex != -1 && tagIndex < minIndex)){
            minIndex = tagIndex;
        }

        if(minIndex == -1){
            return "";
        }

        // Return the Gene
        return string.substring(startIndex, minIndex + 3);
    }

    // Function to find the next stop codons
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int codonIndex = dna.indexOf(stopCodon,startIndex + 3);

        // If there are no codon, return -1
        if(codonIndex == -1){
            return -1;
        }

        // Test and see if distance between currentIndex and codonIndex is dividable by 3.
        while((codonIndex - startIndex) % 3 != 0){
            if(codonIndex == -1){
                return -1;
            }

            // If not dividable by 3, continue searching
            codonIndex = dna.indexOf(stopCodon, codonIndex + 1);
        }

        // Return the value of the stop codon when finished
        return codonIndex;
    }
}
